// init ajax sorting
function initAjaxSorting(){
	var lastClass = 'last';
	var firstClass = 'first';
	var noPostsClass = 'no-posts';
	var loadingClass = 'loading';
	var win = jQuery(window);
	jQuery('.sort-block').each(function(){
		var hold = jQuery(this);
		var form = hold.find('.filter-form');
		var selects = form.find('select');
		var slider = form.find('.slider');
		var ajaxHolder = hold.find('.info-posts');
		var btnLoadMore = hold.find('.load-more');
		var lodare = hold.find('.loader');
		var slider = hold.find('.slider');
		var selectStart = selects.filter('.select-start');
		var selectEnd = selects.filter('.select-end');
		var handleStart = slider.find('.handle-start span');
		var handleEnd = slider.find('.handle-end span');

		// init slider
		slider.slider({
			range: true,
			min: 0,
			max: selectEnd.find('option').length-1,
			values: [selectStart.prop('selectedIndex'), selectEnd.prop('selectedIndex')],
			slide: function( event, ui ) {
				if (hold.hasClass(loadingClass) || ui.values[0] == ui.values[1]) return false;
				selectStart.prop('selectedIndex', ui.values[0]).trigger('change');
				selectEnd.prop('selectedIndex', ui.values[1]);
				refreshHandles();
			}
		});

		// form event handlers
		form.bind('submit', ajaxSubmitForm);
		selects.bind('change', function(){
			form.submit();
		});

		btnLoadMore.click(function(e){
			var href = jQuery(this).attr('href');
			if (!hold.hasClass(noPostsClass) && !hold.hasClass(loadingClass)) {
				hold.addClass(loadingClass);
				jQuery.ajax({
					url: href,
					data: 'ajax=1',
					dataType: 'text',
					error:function(){
						alert('Ajax error!');
					},
					success:function(msg){
						var data = jQuery(msg);
						var newItems = data.filter('.post');
						var nextLink = data.filter('.hidden-loader');
						newItems.hide();
						ajaxHolder.append(newItems);
						newItems.slideDown(500);

						if (nextLink.length) btnLoadMore.attr('href', nextLink.attr('href'));
						else hold.addClass(noPostsClass);

						refreshSideClasses();
						hold.removeClass(loadingClass);
						win.trigger('resize');
					}
				});
			}
			e.preventDefault();
		});
		function refreshSideClasses(){
			ajaxHolder.children().removeClass(lastClass+' '+firstClass).eq(0).addClass(firstClass)
			ajaxHolder.children().eq(ajaxHolder.children().length-1).addClass(lastClass)
		};
		function refreshHandles(){
			handleStart.removeClass().addClass('top position'+selectStart.prop('selectedIndex'));
			handleEnd.removeClass().addClass('bottom position'+selectEnd.prop('selectedIndex'));
			handleStart.text(selectStart.val())
			handleEnd.text(selectEnd.val())
		};
		function ajaxSubmitForm(){
			hold.addClass(loadingClass);
			jQuery.ajax({
				url: form.attr('action'),
				data: form.serialize(),
				type: 'POST',
				success: function(msg){
					var data = jQuery(msg);
					var newItems = data.filter('.post');
					var nextLink = data.filter('.hidden-loader');
					ajaxHolder.empty().append(newItems);
					hold.removeClass(loadingClass+' '+noPostsClass);

					if (nextLink.length) btnLoadMore.attr('href', nextLink.attr('href'));
					else hold.addClass(noPostsClass);

					refreshSideClasses();
					win.trigger('resize');
				},
				error: function(){
					alert('Ajax error!')
				}
			});
			return false;
		};

		refreshHandles();
		refreshSideClasses();
	})
}

