var TMUtility = (function ($, Drupal, window, document, undefined) {
  // locally scoped Object
  var o = {};

  /**
   * Randomize array element order in-place.
   * Using Fisher-Yates shuffle algorithm.
   */
  o.shuffleArray = function (array) {
    for (var i = array.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
    return array;
  };

  /**
   * Returns the height and width of the viewport
   * http://andylangton.co.uk/blog/development/get-viewport-size-width-and-height-javascript
   * @returns {{width: *, height: *}}
   */
  o.viewport = function() {
    var e = window, a = 'inner';
    if (!('innerWidth' in window )) {
      a = 'client';
      e = document.documentElement || document.body;
    }
    return { width : e[ a+'Width' ] , height : e[ a+'Height' ] };
  };

  return o;
})(jQuery, Drupal, this, this.document);


// Marketo form function to hide form wrapper and display success message when form is successfully submitted
function mktoSuccess(form) {
  var formId = form.getId();

  // Add an onSuccess handler
  form.onSuccess(function(values, followUpUrl) {
    // Get the form element and hide it
    document.getElementById("mktoForm-signup-form-" + formId).style.display = "none";
    // show confirm message
    document.getElementById("mktoForm-signup-confirm-" + formId).style.display = "block";

    // Return false to prevent the submission handler from taking the lead to the follow up url
    return false;
  });
}

// Marketo form function to redirect to a url on success
function mktoRedirect(form, url) {
  // Add an onSuccess handler
  form.onSuccess(function(values, followUpUrl) {
    // Take the lead to a different page on successful submit, ignoring the form's configured followUpUrl
    location.href = url;

    // Return false to prevent the submission handler continuing with its own processing
    return false;
  });
}
