(function ($) {
  // DOC READY
  $(function () {

    // Banner shrink on scroll
    $(window).on('scroll', function(){
      if ($(window).scrollTop() > 40) {
        $('body').addClass('small-hero');
      } else {
        $('body').removeClass('small-hero');
      }
    });

    // label class on focus/blur of input
    $('input[type=email], input[type=text]').on('focus blur', function(){
      $(this).siblings('label').toggleClass('focused');
    });

    //set equalheights after images load
    $(window).on('load resize', throttle(function () {
      $('.view-homepage-articles .views-row-1, .hp-primary-articles > .view-header, .homepage-a .hp-secondary-articles > .view-header, .hp-secondary-articles .views-row-2, .hp-secondary-articles .views-row-3, .hp-secondary-articles .views-row-4').conformity({
        mode: 'height'
      });

      setcustomheightoverlays();
    }));


    function setcustomheightoverlays() {
      var overlay1 = $('.homepage-a .view-display-id-block > .view-content > .views-row-2 .node-article.view-mode-homepage_teaser .group-content'),
        overlay2 = $('.homepage-a .view-display-id-block > .view-content > .views-row-3 .node-article.view-mode-homepage_teaser .group-content');

      overlay1.height('auto');
      overlay2.height('auto');

      var height = overlay1.height(),
        height = overlay2.height() > height ? overlay2.height() : height;

      if ($(window).width() > 1150) {
        overlay1.height(height);
        overlay2.height(height);
      }
    }

  });
})(jQuery);

function throttle(fn, threshhold, scope) {
  threshhold || (threshhold = 100);
  var last,
    deferTimer;
  return function () {
    var context = scope || this;

    var now = +new Date,
      args = arguments;
    if (last && now < last + threshhold) {
      // hold on to it
      clearTimeout(deferTimer);
      deferTimer = setTimeout(function () {
        last = now;
        fn.apply(context, args);
      }, threshhold);
    }
    else {
      last = now;
      fn.apply(context, args);
    }
  };
}
;
(function ($) {
  // DOC READY
  $(function () {

    // Process grid fields and their items for interaction
    $('.field-name-field-grid-item > .field-items').each(function(i){
      // check if the number of items is greater than 6
      if ($(this).children().length > 6) {

        // Add unique classes and more button
        $(this).addClass('hide-more gallery-grid-' + i)
          .after('<a href="" data-grid="gallery-grid-' + i + '" class="gallery-grid-button btn red">Show More</a>');

        // Show more grid items when button is triggered
        $('a.gallery-grid-button').on('click', function(e){
          e.preventDefault();
          var t = $(this).data('grid');
          $('.' + t).removeClass('hide-more');
          $(this).remove();
        });
      }
    });

  });
})(jQuery);
;
